import {ElementRef, Injectable} from '@angular/core';
import * as d3 from 'd3';
import {TreeNode} from '../interfaces/interfaces';
import {Selection} from 'd3-selection';
import {HierarchyNode} from 'd3-hierarchy';
import {BehaviorSubject} from 'rxjs';

@Injectable()
export class TreeVisualizationService {

  private treeDataSubject = new BehaviorSubject<TreeNode | null>(null);
  treeData$ = this.treeDataSubject.asObservable();

  private selectedNodeSubject = new BehaviorSubject<TreeNode | null>(null);
  selectedNode$ = this.selectedNodeSubject.asObservable();


  public svgWidth = 928;
  public svgHeight = 500;
  public nodeWidth = 75;
  public nodeHeight = 40;

  constructor() {
  }

  public loadTreeData(): TreeNode {
    return {
      id: '1',
      name: 'Eve',
      level: 0,
      locked: false,
      selected: false,
      reports: ['創世紀報告', '人類起源研究'],
      children: [
        {
          id: '2',
          name: 'Cain',
          parentId: '1',
          level: 1,
          locked: true,
          selected: false,
          reports: ['農業發展計畫']
        },
        {
          id: '3',
          name: 'Seth',
          parentId: '1',
          level: 1,
          locked: false,
          selected: false,
          reports: ['後代傳承方案', '家族發展報告'],
          children: [
            {
              id: '8',
              name: 'Enos',
              parentId: '3',
              level: 2,
              locked: false,
              selected: false,
              reports: ['宗教研究初探']
            },
            {
              id: '9',
              name: 'Noam',
              parentId: '3',
              level: 2,
              locked: false,
              selected: false,
              reports: []
            }
          ]
        },
        {
          id: '4',
          name: 'Abel',
          parentId: '1',
          level: 1,
          locked: false,
          selected: false,
          reports: ['牧羊技術報告']
        },
        {
          id: '5',
          name: 'Awan',
          parentId: '1',
          level: 1,
          locked: false,
          selected: false,
          reports: ['工藝發展史'],
          children: [
            {
              id: '10',
              name: 'Enoch',
              parentId: '5',
              level: 2,
              locked: false,
              selected: false,
              reports: ['城市規劃初步', '建築學基礎']
            }
          ]
        },
        {
          id: '6',
          name: 'Azura',
          parentId: '1',
          level: 1,
          locked: false,
          selected: false,
          reports: []
        },
        {
          id: '7',
          name: 'Awan',  // 故意重複的公司名稱，用於測試「選取同公司提示」功能
          parentId: '1',
          level: 1,
          locked: false,
          selected: false,
          reports: ['另一部門的工藝報告']
        }
      ]
    } as TreeNode;
  } // TODO

  public performAction(): void {} // TODO

  public generateTreeVisualization(data: TreeNode | undefined) {

    // Create hierarchy and apply tree layout
    const root = d3.hierarchy(data) as HierarchyNode<unknown>;

    // Using nodeSize sets the spacing between nodes
    const tree = d3.tree().nodeSize([this.nodeWidth * 1.5, this.nodeHeight * 2]);

    // Apply layout to calculate positions
    tree(root);

    // Create SVG
    const svg = d3.select('.tree-visualization')
      .append('svg')
      .attr('width', this.svgWidth)
      .attr('height', this.svgHeight)
      .attr('viewBox', [0, 0, this.svgWidth, this.svgHeight])
      .attr('style', 'max-width: 100%; height: auto; font: 10px sans-serif;');

    // Create a group to hold all elements with a margin
    const g = svg.append('g')
      .attr('transform', `translate(${this.svgWidth / 2}, ${this.nodeHeight})`);

    // Create links first (so they appear behind nodes)
    const links = g.selectAll('.link')
      .data(root.links())
      .enter()
      .append('path')
      .attr('class', 'link')
      .attr('d', d3.linkVertical()
        .x((d:any) => d.x)  // Use natural x,y for vertical layout
        .y((d:any) => d.y) as any)
      .style('fill', 'none')
      .style('stroke', '#000000')
      .style('stroke-width', '1.5px');

    // Create node groups
    const nodes = g.selectAll('.node')
      .data(root.descendants())
      .enter()
      .append('g')
      .attr('class', 'node')
      .attr('transform', d => `translate(${d.x},${d.y})`);

    // Add rectangles to nodes - centered on node position
    nodes.append('rect')
      .attr('width', this.nodeWidth)
      .attr('height', this.nodeHeight)
      .attr('x', -this.nodeWidth / 2)  // Center the rectangle on the node
      .attr('y', -this.nodeHeight / 2) // Center the rectangle on the node
      .style('fill', '#69b3a2')
      .style('stroke', '#000')
      .attr('rx', 5) // Rounded corners
      .attr('ry', 5);

    // Add text labels
    nodes.append('text')
      .attr('dy', '0.3em')
      .attr('text-anchor', 'middle')
      .text(d => (d.data as TreeNode).name)
      .style('fill', 'white')
      .style('font-size', '12px');

    return svg.node();
  }

  // TODO 處理節點點擊事件
  private handleNodeClick(node: d3.HierarchyNode<TreeNode>) {
    this.selectedNodeSubject.next(node.data);
    // TODO 更新樹狀圖中的選中狀態...
  }

  // TODO 處理按鈕點擊事件
  private handleButtonClick(event: any, buttonData: any) {
    const companyId = d3.select(event.target.closest('.node')).attr('id').replace('company-', '');
    // TODO 根據按鈕類型執行對應操作...
  }

  // TODO 顯示公司報告
  private showCompanyReports(company: TreeNode) {
    // TODO 發出事件通知應用程式顯示報告列表...
  }

  // 尋找相同公司並提示
  highlightSameCompany(companyName: string): void {
    d3.selectAll('.node').classed('highlight-similar', d =>
      d && (d as any).data && (d as any).data.name === companyName
    );
  }


}
