import {ElementRef, Injectable} from '@angular/core';
import * as d3 from 'd3';
import {TreeNode, TreeState} from '../interfaces/interfaces';
import {Selection} from 'd3-selection';
import {HierarchyNode} from 'd3-hierarchy';

@Injectable()
export class TreeVisualizationService {

  public svgWidth = 928;
  public svgHeight = 500;
  public nodeWidth = 75;
  public nodeHeight = 40;

  defaultData: TreeNode = {
    name: 'Eve',
    children: [
      {name: 'Cain'},
      {name: 'Seth', children: [{name: 'Enos'}, {name: 'Noam'}]},
      {name: 'Abel'},
      {name: 'Awan', children: [{name: 'Enoch'}]},
      {name: 'Azura'}
    ]
  };

  constructor() {
  }

  getInitNode(data: TreeNode | undefined) {
    if (!data) {
      data = this.defaultData;
    }
    const root = d3.hierarchy(data) as HierarchyNode<unknown>;
    const tree = d3.tree().nodeSize([this.nodeWidth, this.svgHeight / root.height + 1]);
    tree(root);

    const svg = d3.select('.tree-visualization')
      .append('svg')
      .attr('width', this.svgWidth)
      .attr('height', this.svgHeight)
      .attr('viewBox', [0, 0, this.svgWidth, this.svgHeight])
      .attr('style', 'max-width: 100%; height: auto; font: 10px sans-serif;');
    ;

    const nodes = svg.append('g')
      .attr('class', 'node')
      .selectAll('g')
      .data(root.descendants())
      .join('g')
      .attr('transform', d => `translate(${d.y},${d.x})`);

    nodes.append('rect')
      .attr('width', this.nodeWidth)
      .attr('height', this.nodeHeight)
      .style('fill', 'blue')
      .attr('x', (d, index) => 200 ) // 設定x位置
      .attr('y', (d, index) => 300); // 設定y軸位置

    return svg.node();
  }

  getLinks(data: TreeNode | undefined) {
    if (!data) {
      data = this.defaultData;
    }
    const root = d3.hierarchy(data);
    const links =
      d3.select('.tree-visualization')
        .select('svg')
        .append('g')
      .attr('class', 'link')
      .data(root.links())
      .append('path')
      .attr('class', 'link')
      .attr('d', d3.linkVertical()
        .x((d: any) => d.x)
        .y((d: any) => d.y) as any)
      .style('fill', 'none')
      .style('stroke', '#cccccc')
      .style('stroke-width', '1.5px');

    return links;
  }

}
