import {Component, OnInit, ElementRef, ViewChild, AfterViewInit, OnDestroy} from '@angular/core';
import {Subscription} from 'rxjs';
import * as d3 from 'd3';
import {TreeNode} from '../../interfaces/interfaces';
import {TreeVisualizationService} from '../../services/tree-visualization-service';
import {range} from 'd3';

@Component({
  selector: 'app-tree-visualization',
  templateUrl: './tree-visualization.component.html',
  styleUrls: ['./tree-visualization.component.scss']
})
export class TreeVisualizationComponent implements OnInit, AfterViewInit, OnDestroy {
  @ViewChild('treeContainer', {static: true}) public treeContainer!: ElementRef;
  private subscription: Subscription | undefined;

  selectedNode: TreeNode | null = null;
  private zoom: any;
  private svg: any;

  data: TreeNode = {};

  constructor(private treeVisualizationService: TreeVisualizationService) {
  }

  ngOnInit(): void {
    this.treeVisualizationService.selectedNode$.subscribe(selectedNode => {
      this.selectedNode = selectedNode;
    });
  }

  ngAfterViewInit(): void {
    this.refresh(undefined);
  }

  loadTreeData(): void {
    this.treeVisualizationService.loadTreeData();
     // TODO
  }

  setupZoom(): void {
    this.zoom = d3.zoom()
      .scaleExtent([0.3, 2])
      .on('zoom', (event) => {
        if (this.svg) {
          this.svg.select('g').attr('transform', event.transform);
        }
      });
  }

  applyZoom(): void {
    if (this.svg) {
      this.svg.call(this.zoom);
    }
  }

  zoomIn() {
    this.svg.transition().call(this.zoom.scaleBy, 1.2);
  }

  zoomOut() {
    this.svg.transition().call(this.zoom.scaleBy, 0.8);
  }

  resetZoom() {
    this.svg.transition().call(this.zoom.transform, d3.zoomIdentity);
  }

  refresh(data: TreeNode | undefined): void {
    const initNode =
      this.treeVisualizationService.generateTreeVisualization(data);
    this.treeContainer.nativeElement.appendChild(initNode);
  }

  createNode(): void {
  }

  deleteNode(): void {
  }

  selectedNodes(): void {
  }

  editNode(): void {

  }

  resetView(): void {
    // Implement reset functionality if needed
  }

  ngOnDestroy(): void {
  }
}
<div class="tree-container">
  <div class="tree-controls">
    <button class="reset-button" (click)="resetView()">重置視圖</button>
    <button (click)="createNode()">新增</button>
    <button (click)="deleteNode()">刪除</button>
    <button (click)="selectedNodes()">已選取</button>
  </div>
  <div #treeContainer class="tree-visualization"></div>
</div>
